  var SUSP_DAMPING_2 = 20000;
  var SUSP_MAX = 0.14;
  var WHEEL_MASS = 15;
  var ENGINE_BRAKE_REGION = 0.5;
  var ENGINE_BRAKE_TORQUE = 0.1;
